import axios from 'axios';
import AuthenticationService from './AuthenticationService';

const STUDENT_API_BASE_URL = "http://localhost:8080/api/students"

const config = {headers:{'Authorization': 'Bearer ' + localStorage.getItem("token")}};

class StudentService{
    getStudents(){
        return axios.get(STUDENT_API_BASE_URL, config);
    }

    getStudentsById(studentId){
        return axios.get(STUDENT_API_BASE_URL + "/" + studentId, config);
    }

    createStudent(student){
        return axios.post(STUDENT_API_BASE_URL, student, config);
    }

    updateStudent(student, studentId){
        return axios.put(STUDENT_API_BASE_URL + "/" + studentId, student, config);
    }

    deleteStudent(studentId){
        return axios.delete(STUDENT_API_BASE_URL + "/" + studentId, config);
    }

}

export default new StudentService();
