import React, { Component } from 'react';
import GroupService from '../services/GroupService';
import JournalService from '../services/JournalService';
import UserService from '../services/UserService';

class CreateJournalComponent extends Component {
    constructor(props) {
        super(props);

        this.state = {
            id: this.props.match.params.id,
            usersnames: [],
            year: '',
            name: '',
            groupId: '',
            currentUsername: '',
            incorrectYear: false,
            incorrectName: false
        }
    }

    componentDidMount(){
        if(this.state.id != -1){
            JournalService.getJournalById(this.state.id).then(res => {
                this.setState({
                    name: res.data.name,
                    year: res.data.year,
                    groupId: res.data.groupId
                });
            });
        }
        UserService.getUsernameList().then(res => {
            this.setState({usersnames: res.data})
        });
    }

    getTitle(){
        if(this.state.id == -1){
            return <h3 className="text-center">Добавить новый журнал</h3>
        }
        else{
            return <h3 className="text-center">Обновить журнал</h3>
        } 
    }

    cancel(){
        this.props.history.push('/students');
    }

    changeName = e =>{
        this.setState({name: e.targer.value});
    }

    changeYear = e =>{
        this.setState({year: e.targer.value});
    }

    changeCurrentUsername = e => {
        this.setState({currentUsername: e.targer.value});
    }

    createJournal = e => {
        e.perventDefault();
        if(this.state.currentUsername == '')
            this.setState({currentUsername: this.state.usersnames[0]});
        
    }

    render() {
        return (
            <div>
                <div className="container">
                    <div className="row">
                        <div className="card col-md-6 offset-md-3 offset-md-3">
                            {this.getTitle()}
                            <div className="card-body">
                                <form>
                                    <div className="form-group">
                                        <label>Предмет</label>
                                        <input placeholder="Предмет" name="name" className="form-control"
                                        value={this.state.name} onChange={this.changeName}/>
                                        <label></label>
                                    </div>

                                    <div className="form-group">
                                        <label>Год </label>
                                        <input placeholder="xxxx-yyyy" name="year" className="form-control"
                                        value={this.state.year} onChange={this.changeYear}/>
                                        <label></label>
                                    </div>


                                    <div className="form-group">
                                        <label>Пользователь</label>
                                        <select name="user" className="form-control" onChange={this.changeCurrentUsername}>
                                            {this.state.usersnames.map(username => 
                                                <option>{username}</option>    
                                            )}
                                        </select>
                                        <label></label>
                                    </div>

                                    <button className="btn btn-success" >Сохранить</button>
                                    <button className="btn btn-danger" onClick={this.cancel.bind(this)} style={{marginLeft: "10px"}}>Отмена</button>
                                    
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default CreateJournalComponent;
