import React, { Component } from 'react'
import GroupService from '../services/GroupService'
import JournalService from '../services/JournalService';
import StudentService from '../services/StudentService';
import MarkService from '../services/MarkService';

export default class StatisticComponent extends Component {

    constructor(props) {
        super(props);

        this.state = {
            groups: [],
            journals: [],
            students: [],
            marks: []
        }

    }

    componentDidMount(){
        GroupService.getGroups().then(res => {
            this.setState({groups: res.data});
        })
        JournalService.getJournals().then(res => {
            this.setState({journals: res.data});
        })
    }

    getStudentsCount(groupId){
        let students = [];
        let marks = [];
        let sum = 0;
        StudentService.getStudentsByGroupId(groupId).then(res => {
            students = res.data;
            MarkService.getMarks().then(res1 => {
                marks = res1.data;
                students.map(student => {
                    marks.map(mark => {
                        if(student.id == mark.studentId)
                            sum += mark.mark;
                    })
                })
            });
        });
        return students;
    }

    render() {
        return (
        <div>
            <table border="1" style={{marginTop:'3%'}}>
                <thead>
                    <tr>
                        <td>Куратор</td>
                        <td>Группа</td>
                        <td>Месяц</td>
                        <td>Общая успеваемость</td>
                        <td>Качественная успеваемость</td>
                        <td>Средний балл</td>
                        <td>Посещаемость</td>
                        <td>Количество долгов</td>
                    </tr>
                </thead>
                    <tbody>
                        {this.state.journals.map(journal => 
                            <tr>
                                <td>{journal.curatorName}</td>
                                <td>{journal.groupId}</td>
                                <td>{journal.mouth}</td>
                                <td>{console.log(this.getStudentsCount(journal.groupId))}</td>
                            </tr>
                        )}
                    </tbody>
            </table>
        </div>
        )
    }
}
