import React, { Component } from 'react';
import GroupService from '../services/GroupService';

class GroupListComponent extends Component {
    constructor(props){
        super(props);
        
        this.state = {
            groups:[]
        }
        this.addGroup= this.addGroup.bind(this);
        this.editGroup= this.editGroup.bind(this);
        this.deleteGroup= this.deleteGroup.bind(this);
    }


    componentDidMount(){
        GroupService.getGroups().then(res => {
            this.setState({groups: res.data});
        });
    }


    deleteGroup(id){
        GroupService.deleteGroup(id).then(res => {
            this.setState({groups: this.state.groups.filter(group => group.id !== id)})
        });
    }

    addGroup(){
        this.props.history.push("/add-group/-1");
    }

    editGroup(id){
        this.props.history.push(`/add-group/${id}`);
    }


    render() {
        return (
            <div>
                <h2 className="text-center">Список групп</h2>
                <div className="row">
                    <table className="table table-striped table-bordered">
                        <thead>
                            <tr>
                                <td>Группа</td>
                                <td>Год</td>
                            </tr>
                        </thead>

                        <tbody>
                            {
                                this.state.groups.map(
                                    group =>
                                    <tr key = {group.id} onClick={this.editGroup.bind(this, group.id)}>
                                        <td>{group.groupName}</td>
                                        <td>{group.groupYear}</td>
                                    </tr>
                                )
                            }
                        </tbody>
                    </table>
                </div>
            </div>
        );
    }
}

export default GroupListComponent;
