import axios from 'axios';
import AuthenticationService from './AuthenticationService';

const STUDENT_API_BASE_URL = "http://localhost:8080/api/students"

class StudentService{
    getStudents(){
        return axios.get(STUDENT_API_BASE_URL, localStorage.getItem("token"));
    }

    getStudentsById(studentId){
        return axios.get(STUDENT_API_BASE_URL + "/" + studentId, localStorage.getItem("token"));
    }

    createStudent(student){
        return axios.post(STUDENT_API_BASE_URL, student, localStorage.getItem("token"));
    }

    updateStudent(student, studentId){
        return axios.put(STUDENT_API_BASE_URL + "/" + studentId, student, localStorage.getItem("token"));
    }

    deleteStudent(studentId){
        return axios.delete(STUDENT_API_BASE_URL + "/" + studentId, localStorage.getItem("token"));
    }

}

export default new StudentService();
