import React, { Component } from 'react';
import PropTypes from 'prop-types';
import JournalService from '../services/JournalService';
import GroupService from '../services/GroupService';
import MarkService from '../services/MarkService';
import LessonService from '../services/LessonService';

class JournalDetailsComponent extends Component {
    constructor(props) {
        super(props);

        this.state = {
            id: this.props.match.params.id,
            journal:[],
            groups:[],
            marks:[],
            lessons:[]
        }
    }

    componentDidMount(){
        JournalService.getJournalsById(this.state.id).then((res) => {
            this.setState({journal: res.data});
        });
        GroupService.getGroups().then((res) => {
            this.setState({groups: res.data});
        });
        MarkService.getMarks().then((res) => {
            this.setState({marks: res.data});
        });
        LessonService.getLessons().then((res) => {
            this.setState({lessons: res.data});
        })
    }

    getLessonByJournal(id){
        let result = [];
        this.state.lessons.map(lesson => {
            if(lesson.journalId == id){
                result.push(lesson);
            }
        });
        return result;
    }

    getMarkByJournal(id){
        let result = [];
        this.state.marks.map(mark => {
            if(mark.journalId == id)
                result.push(mark);
        });
        return result;
    }

    render() {
        return (
            <div>
                <h2 className="text-center">Список студентов</h2>
                <div className="row">
                    
                    <table className="table table-striped table-bordered table-responsive">
                        <thead>
                            <tr>
                                <td width="5%">№</td>
                                <td width="25%">ФИО</td>
                                {
                                    this.getLessonByJournal(this.state.id).map(lesson => 
                                        <td>{lesson.date}</td>
                                        )
                                }
                                
                            </tr>
                        </thead>

                        <tbody>
                            <tr>
                            </tr>
                        </tbody>
                    </table>
                </div>
            </div>
        );
    }
}


export default JournalDetailsComponent;
