import React, { Component } from 'react';
import AuthenticationService from '../services/AuthenticationService';

class HeaderComponent extends Component {
    constructor(props){
        super(props);

        this.state = {

        }
    }
    
    logoutLink(){
        if(AuthenticationService.isUserLoggedIn())
        return <li style={{marginLeft:"10px"}}><a id="headerLink" href="/logout">Выйти</a></li>
    }

    loginLink(){
        if(!AuthenticationService.isUserLoggedIn())
        return <li style={{marginLeft:"10px"}}><a id="headerLink" href="/login">Войти</a></li>
    }

    createStudentLink(){
        if(AuthenticationService.isUserLoggedIn())
        return <li style={{marginLeft:"10px"}}><a id="headerLink" href="/add-student/-1">Добавить студента</a></li>;
    }

    createGroupLink(){
        if(AuthenticationService.isUserLoggedIn())
        return <li style={{marginLeft:"10px"}}><a id="headerLink" href="/add-group/-1">Добавить группу</a></li>;
    }

    render() {
        return (
            <div>
                <div>
                <header>
                    <div className="navbar navgab-expand-lg navbar-dark bg-success">
                        <div className="navbar-inner">
                            <a className="navbar-brand" href="/">Онлайн журнал</a>
                        </div>
                        <ul className="nav">
                            <li className="active"><a id="headerLink" href="/students">Список студентов</a></li>
                            <li style={{marginLeft:"10px"}}><a id="headerLink" href="/groups">Список групп</a></li>
                            {this.createStudentLink()}
                            {this.createGroupLink()}
                            {this.loginLink()}
                            {this.logoutLink()}
                            <li></li>
                        </ul>
                    </div>
                </header>
            </div>
            </div>
        );
    }
}

export default HeaderComponent;
