import React, { Component } from 'react';
import PropTypes from 'prop-types';
import JournalService from '../services/JournalService';
import GroupService from '../services/GroupService';

class JournalListComponent extends Component {
    constructor(props) {
        super(props);

        this.state = {
            journals: [],
            groups: []
        }

    }

    componentDidMount(){
        JournalService.getJournals().then((res) => {
            this.setState({journals: res.data});
        });

        GroupService.getGroups().then(res => {
            this.setState({groups: res.data});
        });
    }

    getGroupNameById(id){
        let currentGroup = '';
        this.state.groups.forEach((group) => {
            if(group.id == id){
                currentGroup = group.groupName;
            }
        });
        return currentGroup;
    }


    render() {
        return (
            <div>
                <h2 className="text-center">Список студентов</h2>
                <div className="row">
                    <table className="table table-striped table-bordered">
                        <thead>
                            <tr>
                                <td>Предмент</td>
                                <td>Год</td>
                                <td>Группа</td>
                            </tr>
                        </thead>

                        <tbody>
                            {
                                this.state.journals.map(
                                    journal =>
                                    <tr key = {journal.id} style = {{cursor:"pointer"}}>
                                        <td>{journal.name}</td>
                                        <td>{journal.year}</td>
                                        <td>{this.getGroupNameById(journal.groupId)}</td>
                                    </tr>
                                )
                            }
                        </tbody>
                    </table>
                </div>
            </div>
        );
    }
}


export default JournalListComponent;
