import React, { Component } from 'react';
import GroupService from '../services/GroupService';
import StudentService from '../services/StudentService';

class CreateStudentComponent extends Component {
    constructor(props) {
        super(props);

        this.state = {
            id: this.props.match.params.id,
            firstName: '',
            lastName: '',
            groupId: '',
            groups: []
        }

        this.changeFirstNameHandler = this.changeFirstNameHandler.bind(this);
        this.changeLastNameHandler = this.changeLastNameHandler.bind(this);
        this.changeGroupHandler = this.changeGroupHandler.bind(this);
    }

    componentDidMount(){
        GroupService.getGroups().then(res => {
            this.setState({groups: res.data});
        });

        if(this.state.id == -1){
            return
        }
        else{
            StudentService.getStudents(this.state.id).then((res) => {
                let student = res.data;
                this.setState({
                    firstName: student.firstName,
                    lastName: student.lastName,
                    groupId: student.groupId
                });
            });

        }
        
    }

    saveOrUpdateStudent = e =>{
        e.preventDefault();
        let student = {
            firstName: this.state.firstName, 
            lastName: this.state.lastName, 
            groupId: this.state.groupId}
        if(this.state.id == -1){
            StudentService.createStudent(student).then(res =>{
                this.props.history.push("/students");
            });
        }
        else{
            StudentService.updateStudent(student, this.state.id).then(res => {
                this.props.history.push("/students");
            });
        }
    }

    changeFirstNameHandler = (event) =>{
        this.setState({firstName: event.target.value});
    }

    changeLastNameHandler = (event) =>{
        this.setState({lastName: event.target.value});
    }

    changeGroupHandler = (event) =>{
        this.setState({groupId: event.target.value});
    }

    getTitle(){
        if(this.state.id == -1){
            return <h3 className="text-center">Добавить нового студента</h3>
        }
        else{
            return <h3 className="text-center">Обновить данные об студенте</h3>
        } 
    }

    cancel(){
        this.props.history.push('/students');
    }

    getGroupList(){
        let groups = '';
        this.state.groups.forEach((group) => {
            console.log(groups.groupName);
            groups += <option>{group.groupName}</option>
        });
        return groups;
    }


    render() {
        return (
            <div>
                <div className="container">
                    <div className="row">
                        <div className="card col-md-6 offset-md-3 offset-md-3">
                            {this.getTitle()}
                            <div className="card-body">
                                <form>
                                    <div className="form-group">
                                        <label>Имя</label>
                                        <input placeholder="Имя" name="firstName" className="form-control"
                                        value={this.state.firstName} onChange={this.changeFirstNameHandler}/>
                                        <label></label>
                                    </div>

                                    <div className="form-group">
                                        <label>Фамилия</label>
                                        <input placeholder="Фамилия" name="lastName" className="form-control"
                                        value={this.state.lastName} onChange={this.changeLastNameHandler}/>
                                        <label></label>
                                    </div>

                                    <div className="form-group">
                                        <label>Группа</label>
                                        <input placeholder="Группа" name="group" className="form-control"
                                        value={this.state.groupId} onChange={this.changeGroupHandler}/>
                                        <label></label>
                                    </div>

                                    <div className="form-group">
                                        <label>Группа</label>
                                        <select name="group" className="form-control" onChange={this.changeGroupHandler}>
                                            {this.getGroupList()}
                                        </select>
                                        <label></label>
                                    </div>

                                    <button className="btn btn-success" onClick={this.saveOrUpdateStudent}>Сохранить</button>
                                    <button className="btn btn-danger" onClick={this.cancel.bind(this)} style={{marginLeft: "10px"}}>Отмена</button>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default CreateStudentComponent;
