import React, { Component } from 'react';
import OpenedStudentsService from '../services/OpenedStudentsService';
import UserService from '../services/UserService';

class UsersListComponent extends Component {
    constructor(props){
        super(props)

        this.state = {
            usernames: []
        }
    }
    componentDidMount(){
        UserService.getUsernameList().then(res => {
            this.setState({usernames: res.data});
        });
        UserService.getRoles().then(res => {
            this.setState({roleList: res.data});
        });
    }

    deleteUser(event, username){
        event.preventDefault();
        UserService.getIdByUsername(username).then(res => {
            OpenedStudentsService.getOpenedStudentsByUserId(res.data).then(res1 =>{
                let openedStudents = res1.data;
                openedStudents.map(openedStudent => {
                    OpenedStudentsService.deleteOpenedStudent(openedStudent.id);
                });
                
                UserService.removeUser(username).then(() => {
                    window.location.reload();
                });
            });
        });
    }

    render() {
        return (
            <div>
                <h2 className="text-center">Список пользователей</h2>
                <div className="row">
                    <table className="table table-striped table-bordered">
                        <thead>
                            <tr>
                                <td>Имя</td>
                                <td>Действия</td>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                this.state.usernames.map(username =>
                                <tr key={username}>
                                    <td>{username}</td>
                                    <td><button onClick={(e) => this.deleteUser(e, username)}>Удалить</button></td>
                                </tr>
                            )}
                        </tbody>
                    </table>
                </div>
            </div>
        );
    }
}

export default UsersListComponent;
