import axios from 'axios';
import AuthenticationService from './AuthenticationService';

const GROUP_API_BASE_URL = "http://localhost:8080/api/groups"

const config = {headers:{'Authorization': 'Bearer' + localStorage.getItem("token")}};

class GroupService{
    getGroups(){
        return axios.get(GROUP_API_BASE_URL, config);
    }

    getGroupById(groupId){
        return axios.get(GROUP_API_BASE_URL + "/" + groupId, config);
    }

    createGroup(group){
        return axios.post(GROUP_API_BASE_URL, group, config);
    }

    updateGroup(group, groupId){
        return axios.put(GROUP_API_BASE_URL + "/" + groupId, group, config);
    }

    deleteGroup(groupId){
        return axios.delete(GROUP_API_BASE_URL + "/" + groupId, config);
    }
    
}

export default new GroupService();
