import React, { Component } from 'react'
import UserService from "../services/UserService";

class CreateUserComponent extends Component {
    constructor(props) {
        super(props);

        this.state = {
            username: '',
            passowrd: '',
            role: '',
            rolesArray: []
        }
    }

    componentDidMount(){
        UserService.getRoles(res => {
            this.setState({rolesArray: res.data});
        });
    }

    getRoles(){
    }

    render() {
        return (
            <div>
                <div className="container">
                    <div className="row">
                        <div className="card col-md-6 offset-md-3 offset-md-3">
                            {}
                            <div className="card-body">
                                <form>
                                    <div className="form-group">
                                        <label>Имя пользователя</label>
                                        <input placeholder="Имя пользователя" name="username" className="form-control"
                                        value={this.state.username} onChange={this.changeUsernameHandler}/>
                                        <label></label>
                                    </div>

                                    <div className="form-group">
                                        <label>Пароль</label>
                                        <input placeholder="Пароль" name="password" className="form-control"
                                        value={this.state.password} onChange={this.changePasswordHandler}/>
                                        <label></label>
                                    </div>


                                    <div className="form-group">
                                        <label>Роль</label>
                                        <select name="role" className="form-control" 
                                        onChange={this.changeRoleHandler}>
                                            {this.getRoles()}
                                        </select>
                                        <label></label>
                                    </div>

                                    <button className="btn btn-success" onClick={this.saveOrUpdateStudent}>Сохранить</button>
                                    <button className="btn btn-danger" onClick={this.cancel.bind(this)} style={{marginLeft: "10px"}}>Отмена</button>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
export default CreateUserComponent;
