import React, { Component } from 'react';
import PropTypes from 'prop-types';
import JournalService from '../services/JournalService';
import GroupService from '../services/GroupService';
import MarkService from '../services/MarkService';
import LessonService from '../services/LessonService';
import StudentService from '../services/StudentService';

class JournalDetailsComponent extends Component {
    constructor(props) {
        super(props);

        this.state = {
            id: this.props.match.params.id,
            journal:[],
            groups:[],
            marks:[],
            lessons:[],
            students:[]
        }
        this.changeDateHandler = this.changeDateHandler.bind(this);
        this.changeMarkHandler = this.changeMarkHandler.bind(this);
    }

    componentDidMount(){
        JournalService.getJournalById(this.state.id).then((res) => {
            this.setState({journal: res.data});
        });
        GroupService.getGroups().then((res) => {
            this.setState({groups: res.data});
        });
        MarkService.getMarks().then((res) => {
            this.setState({marks: res.data});
        });
        LessonService.getLessons().then((res) => {
            this.setState({lessons: res.data});
        });
        StudentService.getStudents().then((res) => {
            this.setState({students: res.data});
        })
    }

    getLessonByJournal(id){
        let result = [];
        this.state.lessons.map(lesson => {
            if(lesson.journalId == id){
                result.push(lesson);
            }
        });
        return result;
    }

    getMarkByJournal(markId, studentId){
        let result = [];
        this.state.marks.map(mark => {
            if(mark.journalId == markId && mark.studentId == studentId)
                result.push(mark);
        });
        return result;
    }

    getGroupById(id){
        let result = []
        this.state.groups.map(group => {
            if(group.id == id)
                result = group;
        });
        return result;
    }

    getStudentsNameByGroup(group){
        let result = [];
        this.state.students.map(student => {
            if(student.groupId == group.id)
                result.push(student);
        });
        return result;
    }

    changeDateHandler(event, lesson){
        lesson.date = event.target.value;
        document.location.reload();
        LessonService.updateLesson(lesson, lesson.id);
    }

    changeMarkHandler(event, mark){
        console.log(mark.mark + " " + event.target.value);
        mark.mark = event.target.value;
        document.location.reload();
        MarkService.updateMark(mark, mark.id)
    }

    render() {
        return (
            <div>
                <h2 className="text-center">Список студентов</h2>
                <div className="row">
                    
                    <table className="table table-striped table-bordered table-responsive">

                        <tbody>
                            <tr>
                                <td width="5%">№</td>
                                <td width="25%">ФИО</td>
                                {
                                    this.getLessonByJournal(this.state.id).map(lesson => 
                                        <td key={lesson.id}><input value={lesson.date} onChange={(e) => {this.changeDateHandler(e, lesson)}}></input></td>)
                                }
                            </tr>
                            {this.getStudentsNameByGroup(this.getGroupById(this.state.journal.groupId)).map((student, index) => 
                            <tr key={student.id}>
                                <td>{index + 1}</td>
                                <td>{student.firstName + " " + student.lastName}</td>
                                {
                                    this.getMarkByJournal(this.state.id, student.id).map(mark => 
                                        <td key={mark.id}><input value={mark.mark} onChange={(e) => {this.changeMarkHandler(e, mark)}}></input></td>)
                                }
                            </tr>)}
                            <tr>
                                <td></td>
                                <td></td>
                                {
                                    this.getLessonByJournal(this.state.id).map(lesson => 
                                        <td key={lesson.id}>{lesson.description}</td>
                                )}
                            </tr>
                        </tbody>
                    </table>
                </div>
                
            </div>
        );
    }
}


export default JournalDetailsComponent;
