import React, { Component } from 'react';
import JournalService from '../services/JournalService';
import GroupService from '../services/GroupService';

class JournalListComponent extends Component {
    constructor(props) {
        super(props);

        this.state = {
            journals: [],
            groups: []
        }

    }

    componentDidMount(){
        JournalService.getJournals().then((res) => {
            this.setState({journals: res.data});
        });

        GroupService.getGroups().then(res => {
            this.setState({groups: res.data});
        });
    }

    getGroupNameById(id){
        let currentGroup = '';
        this.state.groups.forEach((group) => {
            if(group.id == id){
                currentGroup = group.groupName;
            }
        });
        return currentGroup;
    }

    addJournal(){
        this.props.history.push("journals/add-journal/-1");
    }

    render() {
        return (
            <div>
                <div className="row">

                <div className="py-3"><button onClick={(e) => this.addJournal()}>Создать журнал</button></div>

                <h2 className="text-center">Мои журналов</h2>
                    <table className="table table-striped table-bordered">
                        <thead>
                            <tr>
                                <td>Предмент</td>
                                <td>Год</td>
                                <td>Группа</td>
                            </tr>
                        </thead>

                        <tbody>
                            {
                                this.state.journals.map(
                                    journal =>
                                    <tr key = {journal.id} style = {{cursor:"pointer"}} onClick={ () => this.props.history.push(`/journals/${journal.id}`)}>
                                        <td>{journal.name}</td>
                                        <td>{journal.year}</td>
                                        <td>{this.getGroupNameById(journal.groupId)}</td>
                                    </tr>
                                )
                            }
                        </tbody>
                    </table>




                    <h2 className="text-center">Список журналов</h2>
                    <table className="table table-striped table-bordered">
                        <thead>
                            <tr>
                                <td>Предмент</td>
                                <td>Год</td>
                                <td>Группа</td>
                            </tr>
                        </thead>

                        <tbody>
                            {
                                this.state.journals.map(
                                    journal =>
                                    <tr key = {journal.id} style = {{cursor:"pointer"}} onClick={ () => this.props.history.push(`/journals/${journal.id}`)}>
                                        <td>{journal.name}</td>
                                        <td>{journal.year}</td>
                                        <td>{this.getGroupNameById(journal.groupId)}</td>
                                    </tr>
                                )
                            }
                        </tbody>
                    </table>

                    
                </div>
            </div>
        );
    }
}


export default JournalListComponent;
