import React, { Component } from 'react';
import JournalService from '../services/JournalService';
import GroupService from '../services/GroupService';
import AuthenticationService from '../services/AuthenticationService';
import UserService from '../services/UserService';

class JournalListComponent extends Component {
    constructor(props) {
        super(props);

        this.state = {
            journals: [],
            groups: [],
            sortedJournals: []
        }

    }

    componentDidMount(){
        JournalService.getJournals().then((res) => {
            this.setState({journals: res.data});
        });

        GroupService.getGroups().then(res => {
            this.setState({groups: res.data});
        });

        if(AuthenticationService.isUserLoggedIn()){
            let username = '';
            let userId = '';
            let sortedJournals = [];
            AuthenticationService.getUsernameByToken().then(res => {
                username = res.data;
                UserService.getIdByUsername(username).then(res => {
                    userId = res.data;
                    this.state.journals.map(journal => {
                        if(journal.userId == userId)
                            sortedJournals.push(journal);
                    });
                    this.setState({sortedJournals: sortedJournals});
                });
            });
        }
    }

    getGroupNameById(id){
        let currentGroup = '';
        this.state.groups.forEach((group) => {
            if(group.id == id){
                currentGroup = group.groupName;
            }
        });
        return currentGroup;
    }

    addJournal(){
        this.props.history.push("journals/add-journal/-1");
    }

    showMyJournals(){
        if(AuthenticationService.isUserLoggedIn())
        return <div>
        <h2 className="text-center">Мои журналы</h2>
            <div className="row">
            <div className="py-3"><button onClick={(e) => this.addJournal()}>Создать журнал</button></div>

            <table className="table table-striped table-bordered">
                <thead>
                    <tr>
                        <td>Предмент</td>
                        <td>Год</td>
                        <td>Группа</td>
                    </tr>
                </thead>

                <tbody>
                    {
                        this.state.sortedJournals.map(
                            journal =>
                            <tr key = {journal.id} style = {{cursor:"pointer"}} onClick={ () => this.props.history.push(`/journals/${journal.id}`)}>
                                <td>{journal.name}</td>
                                <td>{journal.year}</td>
                                <td>{this.getGroupNameById(journal.groupId)}</td>
                            </tr>
                        )
                    }
                </tbody>
            </table>
            </div>
            </div>;
    }

    render() {
        return (
            <div>
                {this.showMyJournals()}

                <div>
                    <h2 className="text-center">Список журналов</h2>
                    <h3 className="text-center">123</h3>
                    <div className="row">
                        <table className="table table-striped table-bordered">
                            <thead>
                                <tr>
                                    <td>Предмент</td>
                                    <td>Год</td>
                                    <td>Группа</td>
                                </tr>
                            </thead>

                            <tbody>
                                {
                                    this.state.journals.map(
                                        journal =>
                                        <tr key = {journal.id} style = {{cursor:"pointer"}} onClick={ () => this.props.history.push(`/journals/${journal.id}`)}>
                                            <td>{journal.name}</td>
                                            <td>{journal.year}</td>
                                            <td>{this.getGroupNameById(journal.groupId)}</td>
                                        </tr>
                                    )
                                }
                            </tbody>
                        </table>

                    </div>
                </div>
            </div>
        );
    }
}


export default JournalListComponent;
