import React, { Component } from 'react';
import UserService from '../services/UserService';

class UsersListComponent extends Component {
    constructor(props){
        super(props)

        this.state = {
            userId: [],
            usernames: [],
            roles: [],
            roleList: []
        }
        this.getUsersRoles = this.getUsersRoles.bind(this);
    }

    getUsersRoles(username){
        let result = '';
        UserService.getIdByUsername(username).then(res => {
            result = res;
            return result;
        });
    }

    componentDidMount(){
        UserService.getUsernameList().then(res => {
            this.setState({usernames: res.data});
        });
        UserService.getRoles().then(res => {
            this.setState({roleList: res.data});
        });
    }



    render() {
        return (
            <div>
                <h4>Поиск и сортировка</h4>
                <div>
                    <input onChange={(e) => {this.setSortedUsernameHandler(e)}} value = {this.state.sortedUsername} placeholder="Имя"></input>
                    <select onChange={this.sortListByOption}>
                        <option value="name">по имени</option>
                        <option value="group">по роли</option>
                    </select>
                </div>
                <h2 className="text-center">Список пользователей</h2>
                <div className="row">
                    <table className="table table-striped table-bordered">
                        <thead>
                            <tr>
                                <td>Имя</td>
                                <td>Роль</td>
                                <td>Действия</td>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                this.state.usernames.map(username =>{
                                    console.log(username + " " + this.getUsersRoles(username));
                                })
                            }
                        </tbody>
                    </table>
                </div>
            </div>
        );
    }
}

export default UsersListComponent;
