import React, { Component } from 'react';
import PropTypes from 'prop-types';
import JournalService from '../services/JournalService';
import GroupService from '../services/GroupService';
import MarkService from '../services/MarkService';
import LessonService from '../services/LessonService';

class JournalDetailsComponent extends Component {
    constructor(props) {
        super(props);

        this.state = {
            journalId: this.props.match.params.id,
            journal:[],
            groups:[],
            marks:[],
            lessons:[]
        }
    }

    componentDidMount(){
        JournalService.getJournalsById(this.state.journalId).then((res) => {
            this.setState({journal: res.data});
        });
        GroupService.getGroups().then((res) => {
            this.setState({groups: res.data});
        });
        MarkService.getMarksByJournal(this.state.journal).then((res) => {
            this.setState({marks: res.data});
        });
        LessonService.getLesson().then((res) => {
            this.setState({lessons: res.data});
        })
    }

    render() {
        return (
            <div>
                <h2 className="text-center">Список студентов</h2>
                <div className="row">
                    
                    <table className="table table-striped table-bordered table-responsive">
                        <thead>
                            <tr>
                                <td width="5%">№</td>
                                <td width="25%">ФИО</td>
                                {
                                    this.state.lessons.map(lesson => <td>{lesson.date}</td>)
                                }
                                
                            </tr>
                        </thead>

                        <tbody>
                            <tr>
                                <td>123</td>
                                <td>321</td>
                                <td>111</td>
                            </tr>
                        </tbody>
                    </table>
                </div>
            </div>
        );
    }
}


export default JournalDetailsComponent;
