import React, { Component } from 'react';
import GroupService from '../services/GroupService';
import StudentService from '../services/StudentService';

class StudentListComponent extends Component {
    constructor(props){
        super(props);
        
        this.state = {
            students:[],
            groups:[]
        }
        this.addStudent= this.addStudent.bind(this);
        this.editStudent= this.editStudent.bind(this);
        this.deleteStudent= this.deleteStudent.bind(this);
    }

    componentDidMount(){
        StudentService.getStudents().then((res) => {
            this.setState({students: res.data});
        });

        GroupService.getGroups().then(res => {
            this.setState({groups: res.data});
        });
    }

    deleteStudent(id){
        StudentService.deleteStudent(id).then(res => {
            this.setState({students: this.state.students.filter(student => student.id !== id)})
        });
    }

    addStudent(){
        this.props.history.push("/add-student/-1");
    }

    editStudent(id){
        this.props.history.push(`/add-student/${id}`);
    }

    getGroupNameById(id){
        this.state.groups.forEach((group) => {
            if(group.id == id){
                console.log(id);
            }
        });
    }

    render() {
        return (
            <div>
                <h2 className="text-center">Список студентов</h2>
                <div className="row">
                    <table className="table table-striped table-bordered">
                        <thead>
                            <tr>
                                <td>Имя</td>
                                <td>Фамилия</td>
                                <td>Группа</td>
                            </tr>
                        </thead>

                        <tbody>
                            {
                                this.state.students.map(
                                    student =>
                                    <tr key = {student.id}>
                                        <td>{student.firstName}</td>
                                        <td>{student.lastName}</td>
                                        <td>{this.getGroupNameById(student.groupId)}</td>
                                        <td>
                                            <button style = {{marginLeft:"10px"}} onClick={() => {this.deleteStudent(student.id)}} className="btn btn-danger">Удалить</button>
                                            <button style = {{marginLeft:"10px"}} onClick={() => {this.editStudent(student.id)}} className="btn btn-info">Обновить</button>
                                        </td>
                                    </tr>
                                )
                            }
                        </tbody>
                    </table>
                </div>
            </div>
        );
    }
}

export default StudentListComponent;
