import React, { Component } from 'react';
import AuthenticationService from '../services/AuthenticationService';
import GroupService from '../services/GroupService';
import StudentService from '../services/StudentService';

class StudentListComponent extends Component {
    constructor(props){
        super(props);
        
        this.state = {
            students:[],
            groups:[]
        }
        this.addStudent= this.addStudent.bind(this);
        this.editStudent= this.editStudent.bind(this);
        this.createStudentLink = this.createStudentLink.bind(this);
    }

    componentDidMount(){
        StudentService.getStudents().then((res) => {
            this.setState({students: res.data});
        });

        GroupService.getGroups().then(res => {
            this.setState({groups: res.data});
        });
    }


    addStudent(){
        this.props.history.push("/add-student/-1");
    }

    editStudent(id){
        this.props.history.push(`/add-student/${id}`);
    }

    getGroupNameById(id){
        let currentGroup = '';
        this.state.groups.forEach((group) => {
            if(group.id == id){
                currentGroup = group.groupName;
            }
        });
        return currentGroup;
    }

    createStudentLink(){
        if(AuthenticationService.isUserLoggedIn())
        return <button style={{marginLeft:"10px"}} onClick={this.addStudent()}>Добавить студента</button>;
    }

    render() {
        return (
            <div>
                <h2 className="text-center">Список студентов</h2>
                <div className="row">
                    
                    {this.createStudentLink()}
                    <table className="table table-striped table-bordered">
                        <thead>
                            <tr>
                                <td>Имя</td>
                                <td>Фамилия</td>
                                <td>Группа</td>
                            </tr>
                        </thead>

                        <tbody>
                            {
                                this.state.students.map(
                                    student =>
                                    <tr key = {student.id} style = {{cursor:"pointer"}} onClick={this.editStudent.bind(this, student.id)}>
                                        <td>{student.firstName}</td>
                                        <td>{student.lastName}</td>
                                        <td>{this.getGroupNameById(student.groupId)}</td>
                                    </tr>
                                )
                            }
                        </tbody>
                    </table>
                </div>
            </div>
        );
    }
}

export default StudentListComponent;
